function Tabtab(selector,options={}){this.container=document.querySelector(selector);if(!this.container){console.error(`Tabtab: Container with selector "${selector}" not found.`);return}this.tabs=Array.from(this.container.querySelectorAll("li a"));if(!this.tabs.length){console.error(`Tabtab: No tabs found in the container`);return}this.panels=this.tabs.map((tab=>{const panel=document.querySelector(tab.getAttribute("href"));if(!panel){console.error(`Tabtab: Panel with href "${tab.getAttribute("href")}" not found.`)}return panel})).filter(Boolean);if(this.tabs.length!==this.panels.length)return;this.opt=Object.assign({remember:false},options);this.paramKey=selector.replace(/[^a-zA-Z0-9]/g,"");this._init()}Tabtab.prototype._init=function(){const params=new URLSearchParams(window.location.search);let value=params.get(this.paramKey);const defaultTab=this.opt.remember&&value&&this.tabs.find((tab=>tab.getAttribute("href").replace(/[^a-zA-Z0-9]/g,"")===value))||this.tabs[0];this._activateTab(defaultTab);this.tabs.forEach((tab=>{tab.onclick=e=>this._handleTabClick(e,tab)}))};Tabtab.prototype._activateTab=function(tab){this.tabs.forEach((tab=>{tab.closest("li").classList.remove("tab--active")}));tab.closest("li").classList.add("tab--active");this.panels.forEach((panel=>panel.hidden=true));const panelActive=document.querySelector(`${tab.getAttribute("href")}`);panelActive.hidden=false;if(this.opt.remember){const params=new URLSearchParams(window.location.search);params.set(this.paramKey,tab.getAttribute("href").replace(/[^a-zA-Z0-9]/g,""));history.replaceState(null,null,`?${params}`)}};Tabtab.prototype._handleTabClick=function(e,tab){e.preventDefault();this._activateTab(tab)};Tabtab.prototype.switch=function(input){let tabToActivate=null;if(!input){console.error("Tabtab: No input provided to switch tabs.");return}if(typeof input==="string"){tabToActivate=this.tabs.find((tab=>{return tab.getAttribute("href")===input}));if(!tabToActivate){console.error(`Tabtab: No tab found with href "${input}"`);return}}else if(this.tabs.includes(input)){tabToActivate=input}if(!tabToActivate){console.error(`Tabtab: No vailid input '${input}'`);return}this._activateTab(tabToActivate)};Tabtab.prototype.destroy=function(){this.tabs.forEach((tab=>{tab.onclick=null;tab.closest("li").classList.remove("tab--active")}));this.panels.forEach((panel=>panel.hidden=false));this.container=null;this.tabs=null;this.panels=null};